import React, { useState } from 'react';
import { AppBar, Toolbar, Typography, Button, makeStyles } from "@material-ui/core";
import { useHistory } from "react-router-dom";

// Definisi atau pengimporan isAuth dan userType di sini
import isAuth, { userType } from "../lib/isAuth";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
}));

const Navbar = (props) => {
  const [selectedButton, setSelectedButton] = useState(null);
  const classes = useStyles();
  let history = useHistory();

  const handleButtonClick = (location) => {
    console.log(location);
    history.push(location);
    setSelectedButton(location);
  };

  const createButton = (label, link, fontSize, fontFamily) => {
    <Button
      color="inherit"
      onClick={() => handleButtonClick(link)}
      style={{
        fontSize,
        fontFamily,
        textDecoration: 'none',
        borderBottom: selectedButton === link ? '2px solid #7bc5d9' : '2px solid transparent',
        transition: 'border-bottom 0.3s ease-in-out',
      }}
      onMouseOver={(e) => {
        e.target.style.borderBottom = '2px solid #7bc5d9';
      }}
      onMouseOut={(e) => {
        if (selectedButton !== link) {
          e.target.style.borderBottom = '2px solid transparent';
        }
      }}
    >
      {label}
    </Button>
  };

  return (
    <AppBar position="fixed" style={{ backgroundColor: '#225f7e' }}>
      <Toolbar style={{ minHeight: '80px' }}>
        <Typography variant="h6" className={classes.title} style={{ fontSize: '32px', fontWeight: '800', fontFamily: 'Titan One, sans-serif' }}>
          <img src="logo1.ico" width="30px" height="auto" /> JOB HARBOR
        </Typography>
        <div style={{ marginTop: '20px' }}>
          {isAuth() ? (
            userType() === 'recruiter' ? (
              <>
                {createButton('Home', '/home', '16px', 'Lato, sans-serif')}
                {createButton('Add Jobs', '/addjob', '16px', 'Lato, sans-serif')}
                {createButton('Posted', '/myjobs', '16px', 'Lato, sans-serif')}
                {createButton('Employees', '/employees', '16px', 'Lato, sans-serif')}
                {createButton('Profile', '/profile', '16px', 'Lato, sans-serif')}
                {createButton('Logout', '/logout', '16px', 'Lato, sans-serif')}
              </>
            ) : (
              <>
                {createButton('Home', '/home', '16px', 'Lato, sans-serif')}
                {createButton('Applied', '/applications', '18px', 'Oswald, sans-serif')}
                {createButton('Profile', '/profile', '18px', 'Oswald, sans-serif')}
                {createButton('Logout', '/logout', '18px', 'Oswald, sans-serif')}
              </>
            )
          ) : (
            <>
              {createButton('Login', '/login', '18px', 'Oswald, sans-serif')}
              {createButton('SignUp', '/signup', '18px', 'Oswald, sans-serif')}
            </>
          )}
        </div>
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
